import React, { useEffect } from 'react'
import { useLocation, useRouteMatch, NavLink } from 'react-router-dom'
import useFecth from 'hooks/useFetch'




const UserProfile = () => {
    const location = useLocation()
    const slug = useRouteMatch().params.slug
    const isFavorites = location.pathname.includes('favorites')
    const apiUrl = `/profiles/${slug}`
    const [{ response }, doFetch] = useFecth(apiUrl)



    useEffect(() => {
        doFetch()
    }, [doFetch])

    if (!response) {
        return null
    }

    return (
        <div className='profile-page'>
            <div className='user-info'>
                <div className='continer'>
                    <div className='row'>
                        <div className='col-xs-12 col-md-10 offset-md-1'>
                            <img className='user-img' alt='' src={response.profile.image} />
                            <h4>{response.profile.username}</h4>
                            <p>{response.profile.bio}</p>
                        </div>
                    </div>
                </div>
            </div>
            <div className='container'>
                <div className='row'>
                    <div className='col-xs-12 col-md-10 offset-md-1'>
                        <div className='articles-toggle'>
                            <ul className='nav nav-pills outline-activle'>
                                <li className='nav-item'>
                                    <NavLink
                                        to={`/profiles/${response.profile.username}`}
                                        className='nav-link'
                                        exact
                                    >
                                        My Posts
                                    </NavLink>
                                </li>
                                <li className='nav-item'>
                                    <NavLink to={`/profiles/${response.profile.username}/favorites`} className='nav-link'>
                                        Favorites Posts
                                    </NavLink>
                                </li>
                            </ul>
                        </div>
                        User Articles
                    </div>
                </div>
            </div>
        </div>
    )
}


export default UserProfile